import React from 'react';
import { CiPlay1 } from 'react-icons/ci';
import { CiCircleInfo } from 'react-icons/ci';

const VideoTitle = () => {
    return (
        <div className='absolute text-white pt-[18%] p-12'>
            <h1 className='text-3xl font-bold'>Khyati Agarwal</h1>
            <p>Contrary to popular belief, Lorem Ipsum is not simply random text.</p>
            <div className='flex mt-8'>
                <button className='flex items-center px-6 py-2 bg-white text-black rounded-md hover:bg-opacity-80'>
                    <CiPlay1 size="24px" />
                    <span>Play</span>
                </button>
                <button className='flex items-center px-6 py-2 bg-white text-black rounded-md'>
                    <CiCircleInfo size="24px" />
                    Watch more
                </button>
            </div>
        </div>
    )
}

export default VideoTitle
