import React from 'react'
import { TMDB_IMG_URL } from '../../constants/tmdb'

const MovieCard = ({ posterPath }) => {
    return (
        <div className='w-48 pr-2'>
            <img src={`${TMDB_IMG_URL}/${posterPath}`} alt='movie banner' />
        </div>
    )
}

export default MovieCard
