import React from 'react'
import { IoIosArrowDropdown } from "react-icons/io";

const Header = () => {
  const user = false;
  return (
    <div className='absolute z-10 flex w-[100vw] items-center justify-between px-6 bg-gradient-to-b from-black '>
      <img className='w-56' src='https://upload.wikimedia.org/wikipedia/commons/thumb/7/7a/Logonetflix.png/1200px-Logonetflix.png' alt='netflix-logo' />
      {
        user && (
          <div className='flex items-center'>
            <IoIosArrowDropdown size="24px" color='white' />
            <h1 className='text-lg font-medium text-white'>Khyati Agarwal</h1>
            <div className='ml-4'>
              <button className='bg-red-800 text-white px-4 py-2'>Logout</button>
              <button className='bg-red-800 text-white px-4 py-2 ml-2'>Search Movie</button>
            </div>
          </div>
        )
      }

    </div>
  )
}

export default Header
